import React, { useContext, useEffect, useState } from 'react';
import { UserContext } from './UserContext';

function Home() {
  const [userEmail, setUserEmail] = useState('');
  const [dynamicEmail, setDynamicEmail] = useState('');

  useEffect(() => {
    const fetchUserEmail = async () => {
      try {
        const response = await fetch('/api/isAuth', {
          method: 'GET',
          credentials: 'include',
        });

        if (response.ok) {
          const { email } = await response.json();
          setUserEmail(email);
        } else {
          throw new Error('Failed to fetch user email');
        }
      } catch (error) {
        console.log(error);
      }
    };

    fetchUserEmail();

    const interval = setInterval(fetchDynamicEmail, 5000);

    return () => clearInterval(interval);
  }, []);

  const fetchDynamicEmail = async () => {
    try {
      const response = await fetch('/api/isAuth', {
        method: 'GET',
        credentials: 'include',
      });

      if (response.ok) {
        const { email } = await response.json();
        setDynamicEmail(email);
      } else {
        throw new Error('Failed to fetch user email');
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleLogout = async () => {
    try {
      const response = await fetch('/api/logout', {
        method: 'DELETE',
        credentials: 'include',
      });

      if (response.ok) {
        // Perform any additional logout-related actions if needed
        console.log('Logout successful');
      } else {
        throw new Error('Failed to logout');
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div id="dashboard" style={{ position: 'relative' }}>
      <button
        onClick={handleLogout}
        style={{
          position: 'absolute',
          top: '10px',
          right: '10px',
          zIndex: '1',
        }}
      >
        Logout
      </button>
      <h1>Dashboard</h1>
      <p style={{ color: 'white' }}>
        Welcome, {dynamicEmail || userEmail}!
      </p>
    </div>
  );
}

export default Home;
