import React, { useContext, useEffect, useState } from 'react';
import { UserContext } from './UserContext';

function Home() {
  const userEmail = useContext(UserContext);
  const [dynamicEmail, setDynamicEmail] = useState('');

  useEffect(() => {
    const interval = setInterval(fetchDynamicEmail, 5000);

    return () => clearInterval(interval);
  }, []);

  const fetchDynamicEmail = async () => {
    try {
      const response = await fetch('/api/isAuth', {
        method: 'GET',
        credentials: 'include',
      });

      if (response.ok) {
        const { email } = await response.json();
        setDynamicEmail(email);
      } else {
        throw new Error('Failed to fetch user email');
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div id="dashboard">
      <h1>Dashboard</h1>
      <p style={{ color: 'white' }}>Welcome, {dynamicEmail || userEmail}!</p>
    </div>
  );
}

export default Home;
